    return (
        <div className="main_wrap">
            <Number/>
            <Buttons/>
        </div>
    );
}


function Number(props) {
    return (
        <div className="number">
            {counter}
        </div>
    );
}

const [counter, setCounter] = useState(0);

function Buttons(props) {
    counter = 0;
    function plus() {
    counter = counter + 1;
    }
    return (
        <div className="buttons">
        <div className="btn_1" onClick={plus}>
            <a href="#">Увеличить</a>
        </div>
            <div className="btn_2">
                <a href="#">Уменьшить</a>
            </div>
            <div className="btn_3">
                <a href="#">Сбросить</a>
            </div>
        </div>
    );
}


